import{_ as e}from"./chunks/logo.39eb08fa.js";import{_ as r,c as a,o as t,a as o}from"./app.b18454ba.js";const u=JSON.parse('{"title":"Overview","description":"","frontmatter":{},"headers":[{"level":2,"title":"The Rust part","slug":"the-rust-part"},{"level":2,"title":"The TypeScript part","slug":"the-typescript-part"}],"relativePath":"guide/README.md","lastUpdated":1662056048000}'),s={name:"guide/README.md"},n=o('<h1 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-hidden="true">#</a></h1><p><a href="https://fadroma.tech" target="_blank" rel="noreferrer">Fadroma</a> is a full-stack application framework for the <a href="https://cosmwasm.com/" target="_blank" rel="noreferrer">CosmWasm</a> ecosystem.</p><p>This guide will show you how to set up a Fadroma project.</p><h2 id="the-rust-part" tabindex="-1">The Rust part <a class="header-anchor" href="#the-rust-part" aria-hidden="true">#</a></h2><p><a href="https://fadroma.tech/rs/fadroma/index.html" target="_blank" rel="noreferrer"><strong>Fadroma Engine</strong></a> is a collection of Rust libraries for developing smart contracts.</p><p>Fadroma Engine includes <a href="https://fadroma.tech/rs/fadroma_proc_derive/index.html" target="_blank" rel="noreferrer"><strong>Fadroma Derive</strong></a>, a collection of procedural macros for cleaner implementation of smart contract internals, and <a href="https://fadroma.tech/rs/fadroma/ensemble/index.html" target="_blank" rel="noreferrer"><strong>Fadroma Ensemble</strong></a>, a library for integration testing of multiple contracts, as well as a range of other useful bits and pieces.</p><h2 id="the-typescript-part" tabindex="-1">The TypeScript part <a class="header-anchor" href="#the-typescript-part" aria-hidden="true">#</a></h2><p><a href="https://fadroma.tech/js/modules/_fadroma_client.html" target="_blank" rel="noreferrer"><strong>Fadroma Client</strong></a> is a library for interfacing with smart contracts from JavaScript or TypeScript.</p><p>Fadroma Client is leveraged by <a href="https://fadroma.tech/js/modules/_fadroma_ops.html" target="_blank" rel="noreferrer"><strong>Fadroma Ops</strong></a>, a library for implementing your custom deployment and operations workflow - from local development to mainnet deployment.</p><p>Fadroma Ops includes <a href="https://fadroma.tech/js/classes/_fadroma_ops.Mocknet.html" target="_blank" rel="noreferrer"><strong>Fadroma Mocknet</strong></a>, a simulated environment for fast full-stack testing of your production builds.</p><p><img src="'+e+'" alt=""></p><div align="center"><p><em>\u0422\u0443\u043A\u0430 \u043C\u0430\u043B\u043A\u043E \u043A\u0430\u0442\u043E \u0442\u0440\u0430\u043A\u0442\u043E\u0440 \u0435 \u0438\u0437\u043B\u044F\u0437\u043B\u0430.</em></p></div>',12),i=[n];function l(p,d,c,m,h,f){return t(),a("div",null,i)}const v=r(s,[["render",l]]);export{u as __pageData,v as default};
